function route(handle, pathname, request, response){
	
	console.log("request  path"+pathname+"。。。。。。");
	if(typeof handle[pathname]==='function'){
		handle[pathname](request, response);
	}else{
		console.log("error");
		response.writeHead(404,{"Content-Type":"text/plain"});
		response.write("404 ");
		response.end();
	}
}
exports.route=route